/* Toggle between adding and removing the "responsive" class to topnav when the user clicks on the icon */
$(document).ready(function() {
  var scroll_pos = 0;
  $(document).scroll(function() {
    scroll_pos = $(this).scrollTop();
    if (scroll_pos > 450) {
      $(".menu-wrap").css('background-color', 'transparent');
    } else {
      $(".menu-wrap").css('background-color', 'transparent');
      $(".topnav").css('background-color', 'transparent');
      $(".topnav").css('opacity', '1');
    }
  });
});

$(document).ready(function() {

  if ($(window).width() <= 850) {
    var windowHeight = window.innerHeight;
    var scroll_pos = 0;
    $(document).scroll(function() {
      scroll_pos = $(this).scrollTop();
      if (((scroll_pos >= (windowHeight*0.45)) && (scroll_pos <= (windowHeight*0.86))) || ((scroll_pos >= (windowHeight*1.77)) && (scroll_pos <= (windowHeight*2)))) {
        $(".logoresize").css({
          'width': '10rem',
          'transition': 'width 0.5s'
        });
      }

      else {
        $(".logoresize").css({
          'width': '5rem',
          'transition': 'width 0.5s'
        });
      }
    });
  }

});

$(window).resize(function() {
  var windowHeight = window.innerHeight;
  if ($(window).width() <= 850) {
    var scroll_pos = 0;
    $(document).scroll(function() {
      scroll_pos = $(this).scrollTop();
      if (((scroll_pos >= (windowHeight*0.45)) && (scroll_pos <= (windowHeight*0.86))) || ((scroll_pos >= (windowHeight*1.77)) && (scroll_pos <= (windowHeight*2.25)))) {
        $(".logoresize").css({
          'width': '10rem',
          'transition': 'width 0.5s'
        });
      } else {
        $(".logoresize").css({
          'width': '5rem',
          'transition': 'width 0.5s'
        });
      }
    });
  } else {
      location.reload();
  }

});

function openModal(){

  var modal = document.getElementById("myModal");

  // Get the button that opens the modal
  var termsAndConditionButton = document.getElementById("myBtn");

  // Get the <span> element that closes the modal
  var span1 = document.getElementsByClassName("close")[0];
  var span2 = document.getElementsByClassName("accept-terms")[0];
  var agreed;
  $("#agreed").click(function() {
                  if ($(this).prop(
                    "checked")) {
                      agreed = 1;
                  } else {
                      agreed = 0;
                  }
              });

  // When the user clicks on the button, open the modal
  termsAndConditionButton.onclick = function() {
    modal.style.display = "block";
  }

  // When the user clicks on <span> (x), close the modal
  span1.onclick = function() {
    modal.style.display = "none";
  }

  span2.onclick = function() {
    if (agreed === 1) {
          localStorage.setItem("cookieBannerDisplay", "true");
          modal.style.display = "none";
        }
    else{
      alert("Agree to the Terms and Conditions.")
    }
  }

  // When the user clicks anywhere outside of the modal, close it
  window.onclick = function(event) {
    if (event.target == modal) {
      modal.style.display = "none";
    }
  }

}
// Get the modal

$(window).ready(function(){
  const containerCookie = $(".container-cookie");
  const viewCookieButton = $("button.view-cookie");
  const acceptCookieButton = $("button.accept-cookie");
  const exitCookieButton = $(".cookie-exit");

  viewCookieButton.click(function(){

    var modal = document.getElementById("myModal");
    var span1 = document.getElementsByClassName("close")[0];
    var span2 = document.getElementsByClassName("accept-terms")[0];
    var agreed;

    $("#agreed").click(function() {
                    if ($(this).prop(
                      "checked")) {
                        agreed = 1;
                    } else {
                        agreed = 0;
                    }
                });

    modal.style.display = "block";

    span1.onclick = function() {
      modal.style.display = "none";
    }

    span2.onclick = function() {
      if (agreed === 1) {
            localStorage.setItem("cookieBannerDisplay", "true");
            containerCookie.removeClass("active");
            modal.style.display = "none";
          }
      else{
        alert("Agree to the Terms and Conditions.")
      }
    }

    // When the user clicks anywhere outside of the modal, close it
    window.onclick = function(event) {
      if (event.target == modal) {
        modal.style.display = "none";
      }
    }
  });

  acceptCookieButton.click(function(){
    containerCookie.removeClass("active");
    localStorage.setItem("cookieBannerDisplay", "true");
  });

  exitCookieButton.click(function(){
    containerCookie.removeClass("active");
  });

  setTimeout(function(){
    if (!localStorage.getItem("cookieBannerDisplay")){
      containerCookie.addClass("active");
    }
  },500);

});

$(document).ready(function(){
  searchParams = new URLSearchParams(window.location.search);
  product = searchParams.get('item');
  if(product){
  $('.quickview-title')[product].click();}
});

